import React, { Component } from 'react';
import './item.css';
import { Glyphicon } from 'react-bootstrap'
import img1 from '../../img/img1.jpg'

class Item extends Component {

    constructor(props) {
        super(props);

        this.state = {
            cardStyle: { favedButton: this.props.favedButton },
            favorited: false,
        };

        this.onClickHandler = this.onClickHandler.bind(this);
        this.onBookmarkClicked = this.onBookmarkClicked.bind(this);
    }

    onClickHandler(event) {
        event.preventDefault();
        this.props.onClick(this.props.id);
    }

    onBookmarkClicked(event) {
        let currentValue = this.state.favorited;
        this.setState({ favorited: !currentValue });
    }

    render() {
        
        let favouriteIcon;

        if (this.state.favorited) {
            favouriteIcon = <button className="favedButton" ><Glyphicon glyph="heart" /></button>
        } else {
            favouriteIcon = <button className="favButton" ><Glyphicon glyph="heart" /></button>
        }

        
        

        return (
            <div className="itemView">

                <span className="itemName">Name</span>
                <img className="itemImage" src={img1} alt="laptop" />

                <div className="description">
                    <p>A wonderful serenity has taken possession of my entire soul, like these sweet mornings of spring which I enjoy with my whole heart. I am alone, and feel the charm of existence in this spot, which was created for the bliss of souls like mine. I am so happy</p>
                </div>

                <div className="itemDetail">
                    <ul>
                        <li>Location: {this.props.items.location}</li>
                        <li>Condition:</li>
                        <li>Mobile: </li>
                        <li className="itemPrice">Price: £500</li>
                    </ul>
                </div>

                <span onClick={this.onBookmarkClicked}>
                    {favouriteIcon}
                </span>
            </div>
        );
    }
}
export default Item;
