import React, { Component } from 'react';
import './App.css';

import Login from './components/login/login'
import Register from './components/register/register'
import Custom from './components/customisation/custom'
import Header from './components/header/header';
import LaptopGrids from './components/laptopSelect/laptopGrids';
import ItemGrids from './components/itemListGrid/itemGrids'


import Data from './Data';
import LaptopType from './components/laptopSelect/laptopType'


class App extends Component {

  modelInfo = []; //model of la
  computerInfo = [] //type of laptop or desktop

  constructor(props) {
    super(props);

    this.state = {
      currentView: "laptop"
    };

    this.handleThumbnailClicked = this.handleThumbnailClicked.bind(this);
    this.handleLaptopType = this.handleLaptopType.bind(this);
    this.handleLoginClicked = this.handleLoginClicked.bind(this);
    this.handleGoToReg = this.handleGoToReg.bind(this);
  }

  handleThumbnailClicked(key) {

    console.log("item with id:" + key + " was clicked");

    if (this.state.currentView !== "itemList")
      return;

    this.setState({ currentView: "itemView" });
    let len = Data.items.length;

    for (let i = 0; i < len; i++) {

      if (Data.items[i].id === key) {
        this.modelInfo.push(Data.items[i]);
      }
    }
  }

  handleLaptopType(laptopType,computerDevice) {

    console.log("item with id:" + laptopType + " was clicked");
    console.log("app " + computerDevice)

    if (this.state.currentView !== "laptop")
      return;

    this.setState({ currentView: "itemList" });
    let len = Data.items.length;

    for (let i = 0; i < len; i++) {

      if (Data.items[i].type === laptopType && Data.items[i].type === computerDevice) {
        this.computerInfo.push(Data.items[i]);
      }
    }

  }
  
  handleLoginClicked(){
    this.setState({ currentView: "login" });
  }

  handleGoToReg(){
    this.setState({ currentView: "register" });
  }

  render() {

    let whatToRender;

    if (this.state.currentView === "itemList") {
      whatToRender = <ItemGrids items={this.computerInfo} onClick={this.handleThumbnailClicked} />
    }
    else if (this.state.currentView === "laptop") {
      whatToRender = <LaptopGrids items={LaptopType.items} onClick={this.handleLaptopType} />
    }
    else if (this.state.currentView === "itemView") {
      whatToRender = <Custom theDevice={this.modelInfo} />
    }
    else if(this.state.currentView ==="login"){
      whatToRender =<Login onClick={this.handleGoToReg}/>
    }
    else if (this.state.currentView === "register"){
      whatToRender = <Register onClick={this.handleLoginClicked}/>
    }
    


    console.log(this.modelInfo)
    console.log(this.computerInfo)

    return (
      <div>
        <Header onClick={this.handleLoginClicked}/>
        {whatToRender}
      </div>
    );
  }

}


export default App;
