import React, { Component } from 'react';
import './custom.css';
import Acer from '../images/acer.jpg'

class Custom extends Component {
    handleSubmit(event) {
        event.preventDefault();
    }

    handleStorage(event) {
        event.preventDefault();
        let desktopCPU = ['intel 7700', 'intel i5 7500', 'intel i3 7100', 'intel pentium g4560']
        let CPUlist = desktopCPU.map(desktopCPU => <option name={desktopCPU}>{desktopCPU}</option>)

        if(event === "SSD"){
            let CPUlist = desktopCPU.map(desktopCPU => <option name={desktopCPU}>{desktopCPU}</option>)
        }else{
            let CPUlist = <option>not SSD</option>
        }
        
    }

    render() {

        return (
            <div className="customisation">
                <form onSubmit={this.handleSubmit} noValidate>

                    <span className="deviceName">Name</span>

                    <img src={Acer} alt="laptop" />

                    <select name="CPU" onChange={this.handleChange} className="CPU">
                        <option name="default" >CPU</option>
                        <option name="i7" >intel i7 7700</option>
                        <option name="i5" >intel i5 7500</option>
                        <option name="i3" >intel i3 7100</option>
                        <option name="pentium" >intel pentium g4560</option>

                    </select>

                    <select name="RAM" onChange={this.handleChange} className="RAM">
                        <option name="default" >Memory</option>
                        <option name="4GB">4GB</option>
                        <option name="8GB">8GB</option>
                        <option name="16GB">16GB</option>
                    </select>

                    <select name="storage" onChange={this.handleStorage} className="storage">
                        <option name="default" >Storage</option>
                        <option name="SSD">SSD</option>
                        <option name="HDD">Hard Drive</option>
                    </select>

                    <select name="storageSize" onChange={this.handleChange} className="storageSize">
                        <option name="default" >Storage size</option>
                        {CPUlist}
                    </select>

                    <select name="GPU" onChange={this.handleChange} className="GPU">
                        <option name="default" >Graphics card</option>
                        <option name="GTX1080TI">GTX1080TI</option>
                        <option name="GTX1080">GTX1080</option>
                        <option name="GTX1070">GTX1070</option>
                        <option name="GTX1070">GTX1060</option>
                        <option name="GTX1070">GTX1050</option>
                    </select>

                    <span className="price">Price: £500</span>
                    <span className="totalPrice">Total Price: 500</span>


                    <button className="checkOut">Check out</button>
                </form>
            </div>
        );
    }
}

export default Custom;
