import React, { Component } from 'react';
import './custom.css';

const formValid = formErrors => {
    let valid = true;

    // check if theres is value in form error 
    Object.values(formErrors).forEach(val => {
        if (val.length > 0) {
            valid = false
        }
    });
    return valid
}

class Custom extends Component {
    constructor(props) {
        super(props);

        this.state = {
            CPU: null,
            RAM: null,
            storage: null,
            size: null,
            GPU: null,
            TotalPrice: null
        }
    }

    handleSubmit = event => {
        event.preventDefault();

        if (formValid(this.state.formErrors)) {
            console.log("submit") //send data 
        }
    };

    handleChange = event => {
        event.preventDefault();
        const { name, value } = event.target;

        let TotalPrice = this.state.TotalPrice
        let price = parseInt(this.props.theDevice[0].price)
        let CPUPrice = 0
        let RAMPrice = 0

        if (name === "CPU") {
            if (value === "default") {
                CPUPrice = CPUPrice + 0
            }

            if (value === "intel i7 7700") {
                CPUPrice = CPUPrice + 300
            }
        }
        else if (name === "RAM") {
            if (value === "default") {
                RAMPrice = RAMPrice + 0
            }
            if (value === "4GB") {
                RAMPrice = RAMPrice + 50
            }
        }

        TotalPrice = price + CPUPrice + RAMPrice

        this.setState({ TotalPrice, [name]: value });
    }

    render() {

        console.log(this.props.theDevice)
        const { TotalPrice } = this.state;

        return (
            <div className="customisation">
                <form onSubmit={this.handleSubmit} noValidate>

                    <span className="deviceName">{this.props.theDevice[0].title}</span>

                    <img src={this.props.theDevice[0].image} alt="laptop" />

                    <select name="CPU" onChange={this.handleChange} className="CPU">
                        <option name="default" >{this.props.theDevice[0].CPU}</option>
                        <option name="pentium" >intel pentium g4560</option>
                        <option name="i3" >intel i3 7100</option>
                        <option name="i5" >intel i5 7500</option>
                        <option name="i7" >intel i7 7700</option>

                    </select>

                    <select name="RAM" onChange={this.handleChange} className="RAM">
                        <option name="default" >{this.props.theDevice[0].RAM}</option>
                        <option name="4GB">4GB</option>
                        <option name="8GB">8GB</option>
                        <option name="16GB">16GB</option>
                    </select>

                    <select name="storage" onChange={this.handleStorage} className="storage">
                        <option name="default" >{this.props.theDevice[0].storage}</option>
                        <option name="SSD">SSD</option>
                        <option name="HDD">Hard Drive</option>
                    </select>

                    <select name="storageSize" onChange={this.handleChange} className="storageSize">
                        <option name="default" >{this.props.theDevice[0].size}</option>
                        <option name="new">500gb</option>
                        <option name="used">1TB</option>
                        <option name="spare">Spare or Repair</option>
                    </select>

                    <select name="GPU" onChange={this.handleChange} className="GPU">
                        <option name="default" >{this.props.theDevice[0].GPU}</option>
                        <option name="GTX1080TI">GTX1080TI</option>
                        <option name="GTX1080">GTX1080</option>
                        <option name="GTX1070">GTX1070</option>
                        <option name="GTX1070">GTX1060</option>
                        <option name="GTX1070">GTX1050</option>
                    </select>

                    <span className="price">Price: £{this.props.theDevice[0].price}</span>
                    <span className="totalPrice">Total Price: {TotalPrice} </span>


                    <button className="checkOut">Check out</button>
                </form>
            </div>
        );
    }
}

export default Custom;
