import React, { Component } from 'react';
import './App.css';
import Login from './components/login/login'
import Register from './components/register/register'
import Custom from './components/customisation/custom'
import Header from './components/header/header';
import ItemGrids from './components/itemListGrid/itemGrids'
import Data from './Data';
import LaptopSelect from './components/laptopSelect/laptopSelect'

class App extends Component {

  deviceInfo = [];

  constructor(props) {
    super(props);

    this.state = {
      currentView: "itemList"
    };

    this.handleThumbnailClicked = this.handleThumbnailClicked.bind(this);
  }

  handleThumbnailClicked(key) {

    console.log("item with id:" + key + " was clicked");

    if (this.state.currentView !== "itemList")
      return;

    this.setState({ currentView: "itemView" });
    let len = Data.items.length;

    for (let i = 0; i < len; i++) {

      if (Data.items[i].id === key) {
        this.deviceInfo.push(Data.items[i]);
      }
    }
  }
  render() {

    let whatToRender;

    if(this.state.currentView === "itemList"){
      whatToRender = <ItemGrids items={Data.items} onClick={this.handleThumbnailClicked} />
    }
    else if(this.state.currentView === "itemView"){
      whatToRender = <Custom theDevice={this.deviceInfo} />
    }

    console.log(this.deviceInfo)

    return (
      <div>
        <Header/>
        <LaptopSelect />
      </div>
    );
  }

}


export default App;
