import React, { Component } from 'react';
import './laptopSelect.css';


class LaptopSelect extends Component {
    constructor(props) {
        super(props);

        this.onClickHandler = this.onClickHandler.bind(this);
    }

    onClickHandler(event) {
        event.preventDefault();
        this.props.onClick(this.props.type);
        console.log(this.props.type)
    }


    render() {

        return (
            <div className="laptopSelect">
                <img src={this.props.image} alt={this.props.imgAlt} />
                <div className="container">
                    <button onClick={this.onClickHandler} className="linkButton"><h4><b>{this.props.title}</b></h4></button>
                    <p>{this.props.description}</p>
                </div>
            </div>
        );
    }
}
export default LaptopSelect;
