import React, { Component } from 'react';
import './ItemList.css';
import { Glyphicon } from 'react-bootstrap'


class ItemList extends Component {

    constructor(props) {
        super(props);

        this.state = {
            cardStyle: { favedButton: this.props.favedButton },
            favorited: false
        };

        this.onClickHandler = this.onClickHandler.bind(this);
        this.onBookmarkClicked = this.onBookmarkClicked.bind(this);
    }

    onClickHandler(event) {
        event.preventDefault();
        this.props.onClick(this.props.id);
    }

    onBookmarkClicked(event) {
        let currentValue = this.state.favorited;
        this.setState({ favorited: !currentValue });
    }

    render() {

        let favouriteIcon;

        if (this.state.favorited) {
            favouriteIcon = <button className="favedButton" ><Glyphicon glyph="heart" /></button>
        } else {
            favouriteIcon = <button className="favButton" ><Glyphicon glyph="heart" /></button>
        }

        return (
            <div className="ItemList">
                <img src={this.props.image} alt={this.props.imgAlt} />
                <div className="container">
                    <button onClick={this.onClickHandler} className="linkButton"><h4><b>{this.props.title}</b></h4></button>
                    <p>{this.props.description.substr(0, 200)}<b> >>></b></p> {/*description character limit 300 */}
                    <ul>
                        <li>Price: Â£{this.props.price}</li>
                    </ul>
                    <span onClick={this.onBookmarkClicked}>
                        {favouriteIcon}
                    </span>
                </div>
            </div>
        );
    }
}
export default ItemList;
